15. find . -name *.java > list16. /opt/sunrts/bin/javac -source 1.4 -target 1.4 -cp ../ri_rtsjBased_j4/src/:cdx @list > javac.out 2>&117. cd ..18. mkdir exe ; cd exe19. cp ../minicdx-j4/rm ../minicdx-j4/rc .20. ../minicdx-j4/gen-ovm.sh > gen.out 2>&121. ./ovm edu.purdue.scj.Launcher cdx.Level1Safelet ../minicdx-j4/cdx.prop > cdx.out 2>&122. (kill it - once it starts printing "done")